import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const D3Chart = ({ data, liquidityData }) => {
  const svgRef = useRef();
  const [zoomLevel, setZoomLevel] = useState(1);
  const [panY, setPanY] = useState(0);
  const [initialViewSet, setInitialViewSet] = useState(false);

  useEffect(() => {
    if (!data || !liquidityData) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const margin = { top: 20, right: 200, bottom: 50, left: 80 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    const g = svg.append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    // Convert timestamps to dates for price data
    const priceData = data.map(d => ({
      date: new Date(d.time * 1000),
      value: d.value
    }));

    // Unified price scale encompassing both price data and liquidity data
    const allPrices = [
      ...priceData.map(d => d.value),
      ...liquidityData.map(d => d.price0)
    ];
    const priceExtent = d3.extent(allPrices);

    // Scales for price line chart
    const xScale = d3.scaleTime()
      .domain(d3.extent(priceData, d => d.date))
      .range([0, width]);

    // Apply zoom and pan to the unified price scale
    const baseYScale = d3.scaleLinear()
      .domain(priceExtent)
      .range([height, 0]);

    // Create zoomed scale
    const priceRange = priceExtent[1] - priceExtent[0];
    const zoomedRange = priceRange / zoomLevel;
    const centerPrice = priceExtent[0] + priceRange * 0.5 + panY * priceRange;

    const yScale = d3.scaleLinear()
      .domain([
        centerPrice - zoomedRange / 2,
        centerPrice + zoomedRange / 2
      ])
      .range([height, 0]);

    // Line generator for price
    const line = d3.line()
      .x(d => xScale(d.date))
      .y(d => yScale(d.value))
      .curve(d3.curveMonotoneX);

    // Draw price line
    g.append("path")
      .datum(priceData)
      .attr("fill", "none")
      .attr("stroke", "#2196F3")
      .attr("stroke-width", 2)
      .attr("d", line);

    // X-axis
    g.append("g")
      .attr("transform", `translate(0,${height})`)
      .call(d3.axisBottom(xScale));

    // Left Y-axis for price (unified scale) - no labels
    g.append("g")
      .call(d3.axisLeft(yScale).tickFormat(() => ""));

    // Right side liquidity chart - inspired by Uniswap's approach
    const liquidityWidth = margin.right - 20;

    // Use the same unified y scale for liquidity positioning
    const liquidityYScale = yScale;

    // X scale for liquidity amounts (bars grow left from right edge)
    const liquidityXScale = d3.scaleLinear()
      .domain([0, d3.max(liquidityData, d => d.activeLiquidity)])
      .range([0, liquidityWidth]);

    // Draw horizontal liquidity bars (growing leftward)
    g.selectAll(".liquidity-bar")
      .data(liquidityData)
      .enter()
      .append("rect")
      .attr("class", "liquidity-bar")
      .attr("x", d => width + margin.right - 10 - liquidityXScale(d.activeLiquidity))
      .attr("y", d => liquidityYScale(d.price0) - 2)
      .attr("width", d => liquidityXScale(d.activeLiquidity))
      .attr("height", 4)
      .attr("fill", "#FF6B35")
      .attr("opacity", 0.7);

    // Right Y-axis for liquidity prices (using same unified scale)
    g.append("g")
      .attr("transform", `translate(${width + margin.right - 10}, 0)`)
      .call(d3.axisRight(yScale).tickFormat(d3.format(".2f")));

    // Add price labels on bars
    g.selectAll(".liquidity-price-label")
      .data(liquidityData.filter((d, i) => i % 10 === 0)) // Show every 10th label to avoid clutter
      .enter()
      .append("text")
      .attr("class", "liquidity-price-label")
      .attr("x", width + margin.right - 5)
      .attr("y", d => liquidityYScale(d.price0))
      .attr("dy", "0.35em")
      .attr("font-size", "8px")
      .attr("fill", "#666")
      .attr("text-anchor", "start")
      .text(d => d.price0.toFixed(0));

    // Setup wheel event handler
    const handleWheel = (event) => {
      event.preventDefault();
      event.stopPropagation();

      // Calculate current view bounds
      const priceRange = priceExtent[1] - priceExtent[0];
      const zoomedRange = priceRange / zoomLevel;
      const currentCenter = priceExtent[0] + priceRange * 0.5 + panY * priceRange;
      const currentMin = currentCenter - zoomedRange / 2;
      const currentMax = currentCenter + zoomedRange / 2;

      // Natural scroll sensitivity based on current view range
      const scrollSensitivity = zoomedRange / 600; // Faster scrolling for larger ranges
      const rawScrollAmount = event.deltaY * scrollSensitivity;

      // Apply scroll (invert deltaY for natural direction)
      const scrollAmount = rawScrollAmount / priceRange; // Normalize to pan range

      setPanY(prev => {
        const newPanY = prev - scrollAmount;

        // Dynamic bounds based on data and zoom level
        const dataMin = Math.min(...allPrices);
        const dataMax = Math.max(...allPrices);
        const halfZoomedRange = zoomedRange / 2;

        // Calculate max pan bounds to keep view within data
        const maxPanUp = (dataMax - halfZoomedRange - (priceExtent[0] + priceRange * 0.5)) / priceRange;
        const maxPanDown = (dataMin + halfZoomedRange - (priceExtent[0] + priceRange * 0.5)) / priceRange;

        // Constrain to bounds
        return Math.max(maxPanDown, Math.min(maxPanUp, newPanY));
      });
    };

    // Add wheel event listener
    const svgElement = svgRef.current;
    if (svgElement) {
      svgElement.addEventListener('wheel', handleWheel, { passive: false });
      return () => svgElement.removeEventListener('wheel', handleWheel);
    }

  }, [data, liquidityData, zoomLevel, panY]);

  // Set reasonable initial view on first load
  useEffect(() => {
    if (!initialViewSet && data && liquidityData && liquidityData.length > 0) {
      const allPrices = [
        ...data.map(d => d.value),
        ...liquidityData.map(d => d.price0)
      ];
      const priceExtent = d3.extent(allPrices);
      const priceRange = priceExtent[1] - priceExtent[0];

      // Filter out extreme outliers for initial view
      const liquidityPrices = liquidityData.map(d => d.price0).sort((a, b) => a - b);
      const percentile20 = liquidityPrices[Math.floor(liquidityPrices.length * 0.2)];
      const percentile80 = liquidityPrices[Math.floor(liquidityPrices.length * 0.8)];

      // Set initial zoom to focus on the 20-80% range of liquidity
      const focusRange = percentile80 - percentile20;
      const desiredZoom = Math.min(priceRange / (focusRange * 3), 10); // Show ~3x the focus range, max 10x zoom

      // Center the view on the middle of the focus range
      const focusCenter = (percentile20 + percentile80) / 2;
      const originalCenter = priceExtent[0] + priceRange * 0.5;
      const panOffset = (focusCenter - originalCenter) / priceRange;

      setZoomLevel(desiredZoom);
      setPanY(panOffset);
      setInitialViewSet(true);
    }
  }, [data, liquidityData, initialViewSet]);

  const handleZoomIn = () => {
    setZoomLevel(prev => Math.min(prev * 1.3, 50));
  };

  const handleZoomOut = () => {
    setZoomLevel(prev => Math.max(prev / 1.3, 0.1));
  };

  const handleResetZoom = () => {
    setZoomLevel(1);
    setPanY(0);
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Zoom Controls */}
      <div style={{ 
        position: 'absolute', 
        top: 10, 
        left: 10, 
        zIndex: 10,
        background: 'rgba(255, 255, 255, 0.9)',
        padding: '8px',
        borderRadius: '4px',
        border: '1px solid #ccc'
      }}>
        <div style={{ marginBottom: '4px', fontSize: '12px', fontWeight: 'bold' }}>
          Zoom: {zoomLevel.toFixed(1)}x
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '4px' }}>
          <button onClick={handleZoomIn} style={{ fontSize: '12px', padding: '2px 8px' }}>
            Zoom In (+)
          </button>
          <button onClick={handleZoomOut} style={{ fontSize: '12px', padding: '2px 8px' }}>
            Zoom Out (-)
          </button>
          <button onClick={handleResetZoom} style={{ fontSize: '12px', padding: '2px 8px' }}>
            Reset
          </button>
          <div style={{ fontSize: '10px', color: '#666', marginTop: '4px', textAlign: 'center' }}>
            Scroll to pan
          </div>
        </div>
      </div>

      <svg
        ref={svgRef}
        width={800}
        height={400}
        style={{ border: '1px solid #ccc' }}
      />
    </div>
  );
};

export default D3Chart;
