import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const Brush = ({
  yScale,
  interactive = true,
  brushExtent,
  setBrushExtent,
  width,
  height,
}) => {
  const brushRef = useRef(null);
  const brushBehavior = useRef(null);
  const brushInProgressRef = useRef(false);

  // Convert [minPrice, maxPrice] to [yMax, yMin] for D3 brush (reversed because SVG y-axis)
  const toYScale = (extent) => {
    if (!extent || extent.length !== 2) return null;
    // extent[0] = minPrice -> bottom of selection (higher y value)
    // extent[1] = maxPrice -> top of selection (lower y value)
    return [yScale(extent[1]), yScale(extent[0])];
  };

  // Convert [yMax, yMin] to [minPrice, maxPrice]
  const toPriceExtent = (selection) => {
    if (!selection || selection.length !== 2) return null;
    // selection[0] = top y position -> maxPrice
    // selection[1] = bottom y position -> minPrice
    return [yScale.invert(selection[1]), yScale.invert(selection[0])];
  };

  // Initialize the brush
  useEffect(() => {
    if (!brushRef.current || !yScale) return;

    brushBehavior.current = d3.brushY()
      .extent([
        [0, 0], // top left - full chart area
        [width, height], // bottom right - full chart area
      ])
      .filter(() => interactive)
      .on('brush', (event) => {
        const { selection } = event;
        brushInProgressRef.current = true;

        if (!selection) {
          return;
        }

        // Convert brush selection to price extent
        const priceExtent = toPriceExtent(selection);
        if (priceExtent) {
          // Update min/max prices immediately during drag
          setBrushExtent([priceExtent[0], priceExtent[1]]);
        }
      })
      .on('end', (event) => {
        brushInProgressRef.current = false;
      });

    // Apply brush behavior to the SVG group
    d3.select(brushRef.current).call(brushBehavior.current);

    // Set initial brush position
    if (scaledExtent[0] !== scaledExtent[1]) {
      d3.select(brushRef.current)
        .transition()
        .call(brushBehavior.current.move, scaledExtent);
    }

    // Style the brush with more visible styling
    d3.select(brushRef.current)
      .selectAll('.selection')
      .attr('stroke', '#2196F3')
      .attr('stroke-width', 2)
      .attr('fill', '#2196F3')
      .attr('fill-opacity', 0.3)
      .attr('cursor', 'grab')
      .style('visibility', 'visible');

    d3.select(brushRef.current)
      .selectAll('.handle')
      .attr('fill', '#2196F3')
      .attr('stroke', '#2196F3')
      .attr('stroke-width', 2)
      .attr('cursor', 'ns-resize')
      .style('visibility', hideHandles ? 'hidden' : 'visible');

    // Make sure overlay is clickable
    d3.select(brushRef.current)
      .selectAll('.overlay')
      .attr('cursor', 'crosshair')
      .attr('fill', 'none')
      .attr('pointer-events', 'all');

  }, [yScale, width, height, interactive, setBrushExtent]);

  // Respond to external brush extent changes
  useEffect(() => {
    if (!brushRef.current || !brushBehavior.current || !yScale || brushInProgressRef.current) {
      return;
    }

    const normalizedExtent = normalizeExtent(brushExtent);
    if (!normalizedExtent) {
      d3.select(brushRef.current).call(brushBehavior.current.clear);
      return;
    }

    const scaledExtent = toYScale(normalizedExtent);
    if (scaledExtent && scaledExtent[0] !== scaledExtent[1]) {
      d3.select(brushRef.current).call(brushBehavior.current.move, scaledExtent);
    }
  }, [brushExtent, yScale]);

  return <g ref={brushRef} />;
};

export default Brush;
